import * as THREE from 'three'
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls.js'
import particleImageSource from '../particule/assets/images/particles/1.png'

/**
 * Textures
 */
const textureLoader = new THREE.TextureLoader()
const particleTexture = textureLoader.load(particleImageSource)

/**
 * Scene
 */
const scene = new THREE.Scene()

/**
 * Sizes
 */
const sizes = {}
sizes.width = window.innerWidth
sizes.height = window.innerHeight

window.addEventListener('resize', () =>
{
    // Save sizes
    sizes.width = window.innerWidth
    sizes.height = window.innerHeight

    // Update renderer
    renderer.setSize(sizes.width, sizes.height)

    // Update camera
    camera.aspect = sizes.width / sizes.height
    camera.updateProjectionMatrix()
})

/**
 * Cursor
 */
const cursor = {}
cursor.x = 0
cursor.y = 0

window.addEventListener('mousemove', (_event) =>
{
    cursor.x = _event.clientX / sizes.width - 0.5
    cursor.y = _event.clientY / sizes.height - 0.5
})

/**
 * Camera
 */
const camera = new THREE.PerspectiveCamera(75, sizes.width / sizes.height, 0.1, 100)
camera.position.z = 10
scene.add(camera)

/**
 * Renderer
 */
const renderer = new THREE.WebGLRenderer()
renderer.setSize(sizes.width, sizes.height)
document.body.appendChild(renderer.domElement)

/**
 * Orbit controls
 */
const controls = new OrbitControls(camera, renderer.domElement)
controls.enableDamping = true
controls.zoomSpeed = 0.3

/**
 * Particles
 */
// Geometry
const particlesGeometry = new THREE.Geometry()

for(let i = 0; i < 1000; i++)
{
    const vertice = new THREE.Vector3(
        (Math.random() - 0.5) * 10,
        (Math.random() - 0.5) * 10,
        (Math.random() - 0.5) * 10
    )
    particlesGeometry.vertices.push(vertice)


// color 

const color = new THREE.Color(
    Math.random() , 
    Math.random() , 
    Math.random() , 
)

particlesGeometry.colors.push(color) 

}

// Material
const particlesMaterial = new THREE.PointsMaterial({
    vertexColors : true , 
    size: 0.4,
    sizeAttenuation: true,
    // color: new THREE.Color(0xff0000),
    // map: particleTexture
    alphaMap: particleTexture,
    transparent: true , 
    depthWrite : false , 
    blending: THREE.AdditiveBlending

})

// Points
const particles = new THREE.Points(particlesGeometry, particlesMaterial)
scene.add(particles)

/**
 * Loop
 */
const loop = () =>
{
    window.requestAnimationFrame(loop)

    //Particles

   // particles.rotation.y += 0.002 rotation

   for ( const vertice of particlesGeometry.vertices) 
   {
       vertice.y += 0.01
       vertice.y += Math.sin(Date.now() * 0.001 + vertice.x * 100) * 0.01
   }

   particlesGeometry.verticesNeedUpdate = true 

    // Update controls
    controls.update()

    // Render
    renderer.render(scene, camera)
}

loop()
