import * as THREE from 'three'
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls.js'
import grassColorImageSource from '../textures/assets/grass/color.jpg' 
import bricksColorImageSource from '../textures/assets/bricks/color.jpg'


/**texture 
 * 
*/
/** 
const grassColorImage = new Image ()
const grassColorTexture = new THREE.Texture(grassColorImage)  

grassColorImage.addEventListener('load' , () => 

{
    console.log('charg√©e !')

    grassColorTexture.needsUpdate = true 
})

grassColorImage.src = grassColorImageSource */
const textureLoader = new THREE.TextureLoader()
const grassColorTexture = textureLoader.load(grassColorImageSource) 
const bricksColorTexture = textureLoader.load(bricksColorImageSource)

grassColorTexture.wrapS = THREE.RepeatWrapping 
grassColorTexture.wrapT = THREE.RepeatWrapping

grassColorTexture.repeat.x = 4
grassColorTexture.repeat.y = 4

/*magfilter pour pixeliser 
const grassColorTexture = textureLoader.load(grassColorImageSource)
// grassColorTexture.magFilter = THREE.LinearFilter
grassColorTexture.magFilter = THREE.NearestFilter*/




/**
 * Scene
 */
const scene = new THREE.Scene()

/**
 * Sizes
 */
const sizes = {}
sizes.width = window.innerWidth
sizes.height = window.innerHeight

window.addEventListener('resize', () =>
{
    // Save sizes
    sizes.width = window.innerWidth
    sizes.height = window.innerHeight

    // Update renderer
    renderer.setSize(sizes.width, sizes.height)

    // Update camera
    camera.aspect = sizes.width / sizes.height
    camera.updateProjectionMatrix()
})

/**
 * Cursor
 */
const cursor = {}
cursor.x = 0
cursor.y = 0

window.addEventListener('mousemove', (_event) =>
{
    cursor.x = _event.clientX / sizes.width - 0.5
    cursor.y = _event.clientY / sizes.height - 0.5
})

/**
 * Camera
 */
const camera = new THREE.PerspectiveCamera(75, sizes.width / sizes.height, 0.1, 100)
camera.position.z = 10
scene.add(camera)

/**
 * House
 */
const houseGroup = new THREE.Group()
scene.add(houseGroup)

const walls = new THREE.Mesh(
    new THREE.BoxGeometry(5, 2.5, 5, 1, 1, 1),
    new THREE.MeshBasicMaterial({ map : bricksColorTexture})
)
walls.position.y = 1.25
houseGroup.add(walls)

const grass = new THREE.Mesh(
    new THREE.PlaneGeometry(15, 15, 1, 1),
    new THREE.MeshBasicMaterial({ map: grassColorTexture})
)
grass.rotation.x = - Math.PI * 0.5
houseGroup.add(grass)

const roof = new THREE.Mesh(
    new THREE.ConeGeometry(4.2, 1, 4, 1),
    new THREE.MeshBasicMaterial({ color: 0x885522 })
)
roof.position.y = 3
roof.rotation.y = Math.PI * 0.25
houseGroup.add(roof)

const door = new THREE.Mesh(
    new THREE.BoxGeometry(0.2, 2, 1),
    new THREE.MeshBasicMaterial({ color: 0xff8866 })
)
door.position.x = - 2.5
door.position.y = 1
houseGroup.add(door)

const bush1 = new THREE.Mesh(
    new THREE.SphereGeometry(0.5, 16, 16),
    new THREE.MeshBasicMaterial({ color: 0x228833, wireframe: true })
)
bush1.position.x = - 2.7
bush1.position.y = 0.25
bush1.position.z = 0.8
houseGroup.add(bush1)


const bush2 = new THREE.Mesh(
    new THREE.SphereGeometry(0.5, 16, 16),
    new THREE.MeshBasicMaterial({ color: 0x228833, wireframe: true })
)
bush2.position.x = - 2.7
bush2.position.y = 0.25
bush2.position.z = -0.8
houseGroup.add(bush2)

/**
 * Renderer
 */
const renderer = new THREE.WebGLRenderer()
renderer.setSize(sizes.width, sizes.height)
document.body.appendChild(renderer.domElement)

/**
 * Orbit controls
 */
const controls = new OrbitControls(camera, renderer.domElement)
controls.enableDamping = true
controls.zoomSpeed = 0.3

/**
 * Loop
 */
const loop = () =>
{
    window.requestAnimationFrame(loop)

    // Update controls
    controls.update()

    // Render
    renderer.render(scene, camera)
}

loop()
